//最好不要用attr方法增加属性，否则会出现第三次点击不生效的情况，用prop方法较好
$('input[name="selectall"]').click(function(){  
        //alert(this.checked);  
        if($(this).is(':checked')){  
            $('input[name="stuCheckBox"]').each(function(){  
